;; lets count some brackets

(require '[clojure.string :as str])

(defn count-brackets [brackets]
  (loop [count 0 remaining-brackets brackets]
    (cond
      (str/blank? remaining-brackets) count
      (= \( (first remaining-brackets)) (recur (+ count 1) (subs remaining-brackets 1))
      (= \) (first remaining-brackets)) (recur (- count 1) (subs remaining-brackets 1)))))

(defn find-basement-position [brackets]
  (loop [count 0 position 0 remaining-brackets brackets]
    (cond
      (str/blank? remaining-brackets) position
      (= count -1) position
      (= \( (first remaining-brackets)) (recur
                                         (inc count) (inc position) (subs remaining-brackets 1))
      (= \) (first remaining-brackets)) (recur
                                         (dec count) (inc position) (subs remaining-brackets 1)))))
      
